		[SupportedOSPlatform("windows6.0.6000")]
		internal static unsafe winmdroot.Foundation.BOOL CheckRemoteDebuggerPresent(SafeHandle hProcess, out winmdroot.Foundation.BOOL pbDebuggerPresent)
		{
			bool hProcessAddRef = false;
			try
			{
				fixed (winmdroot.Foundation.BOOL* pbDebuggerPresentLocal = &pbDebuggerPresent)
				{
					winmdroot.Foundation.HANDLE hProcessLocal;
					if (hProcess is object)
					{
						hProcess.DangerousAddRef(ref hProcessAddRef);
						hProcessLocal = (winmdroot.Foundation.HANDLE)hProcess.DangerousGetHandle();
					}
					else
						hProcessLocal = default(winmdroot.Foundation.HANDLE);
					winmdroot.Foundation.BOOL __result = PInvoke.CheckRemoteDebuggerPresent(hProcessLocal, pbDebuggerPresentLocal);
					return __result;
				}
			}
			finally
			{
				if (hProcessAddRef)
					hProcess.DangerousRelease();
			}
		}

