		[SupportedOSPlatform("windows6.0.6000")]
		internal static unsafe void* VirtualAllocExNuma(SafeHandle hProcess, void* lpAddress, nuint dwSize, winmdroot.System.Memory.VIRTUAL_ALLOCATION_TYPE flAllocationType, uint flProtect, uint nndPreferred)
		{
			bool hProcessAddRef = false;
			try
			{
				winmdroot.Foundation.HANDLE hProcessLocal;
				if (hProcess is object)
				{
					hProcess.DangerousAddRef(ref hProcessAddRef);
					hProcessLocal = (winmdroot.Foundation.HANDLE)hProcess.DangerousGetHandle();
				}
				else
					hProcessLocal = default(winmdroot.Foundation.HANDLE);
				void* __result = PInvoke.VirtualAllocExNuma(hProcessLocal, lpAddress, dwSize, flAllocationType, flProtect, nndPreferred);
				return __result;
			}
			finally
			{
				if (hProcessAddRef)
					hProcess.DangerousRelease();
			}
		}

